package com.leetcode

import org.hamcrest.MatcherAssert.assertThat
import org.hamcrest.Matchers.containsInAnyOrder
import org.junit.jupiter.api.Test

class P187Test {
    val p = P187()

    @Test
    fun `example 01`() {
        assertThat(
            p.findRepeatedDnaSequences("AAAAACCCCCAAAAACCCCCCAAAAAGGGTTT"),
            containsInAnyOrder("AAAAACCCCC", "CCCCCAAAAA")
        )
    }

    @Test
    fun `example 02`() {
        assertThat(p.findRepeatedDnaSequences("AAAAAAAAAAAAA"), containsInAnyOrder("AAAAAAAAAA"))
    }

    @Test
    fun `example 03`() {
        assertThat(
            p.findRepeatedDnaSequences(""),
            containsInAnyOrder("GTTCTCACTA")
        )
    }

}
